import { useState } from "react";
import { Table, Form } from "react-bootstrap";

export const TransTable = ({ transList }) => {
  const [idsToDelete, setIdsToDelete] = useState([]);

  const handleOnSelect = (e) => {
    const { checked, value } = e.target;
    if (checked) {
      // add
      setIdsToDelete([...idsToDelete, value]);
    } else {
      // ids to remove
      setIdsToDelete(idsToDelete.filter((item) => item._id !== value));
    }
    console.log(checked, value);
  };
  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th className="d-flex">
            <Form.Check />
            Date
          </th>
          <th>Title</th>
          <th>Income</th>
          <th>Expenses</th>
        </tr>
      </thead>
      <tbody>
        {transList.map(({ _id, title, date, amount, type }) => (
          <tr>
            <td className="d-flex">
              <Form.Check value={_id} onChange={handleOnSelect} />
              {date.slice(0, 10)}
            </td>
            <td>{title}</td>

            {type === "income" ? (
              <>
                <td className="text_success">{amount}</td>
                <td></td>
              </>
            ) : (
              <>
                <td></td>
                <td className="text-danger">-{amount}</td>
              </>
            )}
          </tr>
        ))}
        <tr className="fw-bolder">
          <td colSpan={3} className="text-end">
            Total Balance
          </td>
          <td>
            {transList.reduce((acc, { amount, type }) => {
              return type === "income" ? acc + amount : acc - amount;
            }, 0)}
          </td>
        </tr>
      </tbody>
    </Table>
  );
};
