import { useState } from "react";
import { Table, Form, Button } from "react-bootstrap";
import { deleteTrans } from "../helper/axiosHelper";

export const TransTable = ({ transList }) => {
  const [idsToDelete, setIdsToDelete] = useState([]);
  const [resp, setResp] = useState({});

  const handleOnSelect = (e) => {
    const { checked, value } = e.target;
    if (checked) {
      // add
      setIdsToDelete([...idsToDelete, value]);
    } else {
      // ids to remove
      setIdsToDelete(idsToDelete.filter((item) => item !== value));
    }
  };

  const handleOnAllSelect = (e) => {
    const { checked } = e.target;
    if (checked) {
      // add all the ids to delete
      console.log("transList");
      const ids = transList.map(({ _id }) => _id);
      setIdsToDelete(ids);
    } else {
      setIdsToDelete([]);
    }
  };

  const handleOnDelete = async () => {
    if (window.confirm("Are you sure you want to delete?")) {
      return;
    }
    const result = await deleteTrans(idsToDelete);
    setResp(result);

    if (result?.status === "success") {
      getAllTrans();
      setIdsToDelete([]);
    }
  };
  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th className="d-flex">
            <Form.Check
              checked={idsToDelete.length === transList.length}
              onChange={handleOnAllSelect}
            />
            {""}
            Date
          </th>
          <th>Title</th>
          <th>Income</th>
          <th>Expenses</th>
        </tr>
      </thead>
      <tbody>
        {transList.map(({ _id, title, date, amount, type }) => (
          <tr>
            <td className="d-flex">
              <Form.Check
                value={_id}
                checked={idsToDelete.includes(_id)}
                onChange={handleOnSelect}
              />
              {date.slice(0, 10)}
            </td>
            <td>{title}</td>

            {type === "income" ? (
              <>
                <td className="text_success">{amount}</td>
                <td></td>
              </>
            ) : (
              <>
                <td></td>
                <td className="text-danger">-{amount}</td>
              </>
            )}
          </tr>
        ))}
        <tr className="fw-bolder">
          <td colSpan={3}>
            <div className="d-flex justify-conten-between" />
            {idsToDelete.length > 0 && (
              <Button onClick={handleOnDelete} variant="danger">
                Delete {idsToDelete.length}
                transactions
              </Button>
            )}
            Total Balance
          </td>
          <td>
            {transList.reduce((acc, { amount, type }) => {
              return type === "income" ? acc + amount : acc - amount;
            }, 0)}
          </td>
        </tr>
      </tbody>
    </Table>
  );
};
